INSERT INTO `mission_detail` (`mission_id`, `pid`, `title`, `price`, `start_date`, `end_date`, `city`, `area`, `location_detail`, `description`, `mission_type`, `payment_type`, `post_user_id`, `post_date`, `update_date`, `mission_status`, `contact_morning`, `contact_noon`, `contact_night`) VALUES
(1, 'df1254', '桃園10/30~11/02貓咪代餵', 800, '2023-10-30', '2023-11-02', '桃園市', '中壢區', '新生路', '<li>幫我照顧兩隻貓咪</li><li><p>你需要：</p><p>打掃貓咪弄髒的地方（貓咪有嘔吐的話要幫我撿）</p><p>清理貓砂</p><p>清洗貓咪的飲水機</p><p>需幫我餵罐頭</p><p>確認網路是否正常（如果斷線要幫我重新插插頭）</p></li><li>一次約20分鐘內可以完成</li><li>家中有監視器不介意再應徵</li><li>希望你家中也有寵物，絕對不能使用任何帶有強迫或暴力的動作對貓咪，喜歡貓咪佳</li>', 1, 1, 5, '2023-09-02', '2023-09-02', 1, 0, 1, 1),
