INSERT INTO `mission_helper_info` (`user_id`, `name`, `Introduction`, `email`, `phone`, `job_description`, `service_county`, `feed_service`, `house_service`, `beauty_service`, `feed_price`, `house_price`, `beauty_price`) VALUES
(1, '朴蘿菠', '10多年的養貓經驗，很喜歡小動物們，從小時候開始就養過貓咪、孔雀魚、烏龜、白文鳥，喜歡陪伴、照顧他們，一起玩耍，看著他們健康成長。\n毛孩都有他自己的個性，也需要被尊重，好好對待。\n毛小孩的單純，摸摸他們就能感受到美好，是一種難以言喻的愛～有他們的陪伴很幸福❤️', 'user1@example.com', '0952487632', '<div class=\"d-flex flex-column gap-2\">\n                  <p class=\"size-7\">&#128205; 住宿部分預約須知：</p>\n                  <p>1.需有按時吃驅蟲藥以及心絲蟲</p>\n                  <p>2.需自備碗、飼料（鮮食）、牽繩、自家小被被（可有可無）</p>\n                  <p>\n                    3.需誠實告知狗狗習性，如：護食、不喜歡公狗、比較敏感、有嚴重區域，不喜歡被摸屁屁、耳朵等等\n                  </p>\n                  <p>4.信任彼此 ⚠️毛孩如有任何過敏體質或是食物請告知</p>\n\n                  <p class=\"size-7 mt-2\">&#128205; 家中環境介紹：</p>\n                  <p>\n                    家裡有廣大的空地（有安排防護）以及24小時過濾水提供（不是自來水或地下水）陽光充足\n                  </p>\n                  <p>\n                    住宿不關籠（如有關籠需求提前告知）、睡覺睡室內玩耍再室外，環境不是最乾淨但絕對不無聊，安排多項供貓咪玩耍的玩具及高空環境\n                  </p>\n                  <p>\n                    老寶貝也可來住宿，有獨立空間感不受打擾，清幽住宿，安心養老。\n                  </p>\n                  <p>✅全職的保姆，寶貝24小時陪伴照顧</p>\n                  <p>✅有照顧奶貓7年以上經驗</p>\n                  <p>✅曾在中途之家擔任志工多年</p>\n                  <p class=\"size-7 mt-3\">&#128205; 到府照顧服務說明：</p>\n                  <p>🕍毛孩活動環境整潔（大小便狀況皆會說明）</p>\n                  <p>\n                    🍖餵食、換水補水（寶貝的罐罐需要秤重可以提前告知我喔～）\n                  </p>\n                  <p>💡毛孩狀況照實回報，主人安心出門</p>\n                  <p>🤹🏼‍♀️陪伴娛樂玩耍🎢</p>\n                  <p>📸隨時側拍毛孩給家長看，讓家長身歷其中🥰</p>\n                  <p>✅✅進門前皆會消毒雙手並且告訴家長已抵達家中服務</p>\n                  <p>\n                    ⭐️⭐️第一次預約可找時間安排一個免費家訪，與毛孩彼此互相了解、熟悉。也讓家長更認識我，在出遊期間能更安心❤️❤️如果服務日期接近，家訪時間碰不上，會詳細詢問毛孩狀況以及在家中習慣…等等🐶🐱\n                  </p>\n                </div>', '台北市', 0, 0, 1, 300, 900, 400),
